import React, { useState } from "react";

const DropDown = ({ options }) => {
    const [selectedOptionDate, setSelectedOptionDate] = useState("January");

    const handleOptionChange = (event) => {
        setSelectedOptionDate(event.target.value);
    };

    return(
        <div>
            <h2> Date </h2>
            <label htmlFor="dropdown">Select a date</label>
            <select
            id="dropdown"
            value={selectedOptionDate}
            onChange={handleOptionChange}
            >
            <option value="January">January</option>
            <option value="February">February</option>
            <option value="March">March</option>
            <option value="April">April</option>
            </select>
            <p>Selected Option: {selectedOptionDate}</p>
        </div>
        
    )
}

export default DropDown;
