import React from "react";
import "./button.css";

const Button = ({text, img = ""}) => {
    const handleClick= () => {
        alert("Button Clicked!");
    };

    return (
        <button onClick={handleClick}>
            <div className="button-text">{text}</div>
            {img && <img src={img} alt="Button icon" />}
        </button>
    );
}

export default Button;
